$( function() {
    function log( value, id ) {

        $( "<span class='badge badge-success' data-id="+id+">" ).text( value ).prependTo( "#log" );
        $( "#log" ).scrollTop( 0 );
    }

    $( "#skills-field").autocomplete({
        source: function(request, response) {
            $.ajax({
                url: "http://api.dataatwork.org/v1/skills/autocomplete",
                data: {
                    begins_with: request.term
                },
                success: function (data) {
                    var results = data.map(function (datum) {
                        return {value: datum.suggestion, id: datum.uuid};
                    });

                    response(results);
                }
            });
        },
        minLength: 2,
        select: function( event, ui ) {
            log(ui.item.value, ui.item.id );
        }
    });
} );
